import React, { useState, useMemo } from "react";

export const getPagesArray = (totalPages) => {
  const sortedPosts = useMemo(() => {
    let result = [];
    for (let i = 0; i < totalPages; i++) {
      result.push(i + 1);
    }
  });
  return sortedPosts;
};
export default getPagesArray;
