import React, { useMemo } from "react";

export const usePaggination = (totalPages, filter, page) => {
  const getPagesArray = useMemo(() => {
    let result = [];
    for (let i = 0; i < totalPages; i++) {
      result.push(i + 1);
    }
  }, [filter, getPagesArray]);
  return getPagesArray;
};
